package day20;

public class BeforeLoopEra {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int sum=0;
int x=1;
if (x<5) {
	sum=sum+x;
	System.out.println(sum );
	x++;}
	if(x<5) {
	sum=sum+x;
	System.out.println(sum);
	x++;}
	if (x<5) {
		sum=sum+x;
		System.out.println(sum );
		x++;}
		if (x<5) {
			sum=sum+x;
			System.out.println(sum );
			x++;}


		// bunlarin hepsii yapmak yerine ayri ayri loop kullaniyoruz 
		
	}

}
