package day20;

public class InfiniteforLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i <0 ; i++) {
			System.out.println("i " + i);
		}
		
		System.out.println("end");
		
		for (int i = 0; i <10 ; i--) {
			System.out.println("i " + i);
		}
		
		System.out.println("The end");
			
		
		
		
	}

}
