package day20;

public class LoopBreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// break satetemen is used to break out of switch and 
		// loops
	int x=10;	
do {
	
	System.out.println(x);
	x++;
	// if eklenir while loop a sadece break koy bu sefer araya bu 
	//eklenince 100 yerne 50 ya kadar saydi 
	if(x>50) {
		break;
	}
	}while (x<100);




}
}